package by.academy.classwork.lesson4;

public class Task3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String str ="I Like Java !!!";
		
		System.out.println("Задание б: " + str.charAt(str.length()-1));
		
		System.out.println("Задание в: " + str.endsWith("!!!"));
		
		System.out.println("Задание г: " + str.startsWith("I Like"));
		
		System.out.println("Задание д: " + str.contains("Java"));
		
		System.out.println("Задание e: " + str.indexOf("Java"));
		
		System.out.println("Задание ё: " + str.replace('a','o'));
		
		System.out.println("Задание ж: " + str.toLowerCase());
		
		System.out.println("Задание з: " + str.toUpperCase());
		
		System.out.println("Задание и: " + str.substring(2, 6));
		
		
		
		
		
		
		
	}

}
